const minutesEl=document.querySelector("#minutes"),secondsEl=document.querySelector("#seconds");let time=1800;function startTimer(){interval=setInterval((()=>{time--,displayTime(),time<=0&&stopTimer()}),1e3)}function stopTimer(){interval=clearInterval(interval)}function displayTime(){const t=Math.floor(time/60),e=time%60,n=`${e<10?0:""}${e}`,i=`${t<10?0:""}${t}`;minutesEl.textContent=i,secondsEl.textContent=n}startTimer();